mapper.update:
UPDATE raffle_activity_account_month
SET month_count_surplus = month_count_surplus - 1,
    update_time         = NOW()
WHERE user_id = #{userId}
  AND activity_id = #{activityId}
  AND month = #{month}
  AND month_count_surplus > 0

mapper.select:
SELECT user_id, activity_id, month, month_count, month_count_surplus
FROM raffle_activity_account_month
WHERE user_id = #{userId}
  AND activity_id = #{activityId}
  AND month = #{month}
LIMIT 1
