mapper.update:
UPDATE raffle_activity_order
SET state       = 'completed',
    update_time = NOW()
WHERE user_id = #{userId}
  AND out_business_no = #{outBusinessNo}
  AND state = 'wait_pay'

mapper.select:
SELECT user_id, sku, activity_id, activity_name, strategy_id,
       order_id, order_time, total_count, day_count, month_count,
       pay_amount, state, out_business_no, create_time, update_time
FROM raffle_activity_order
WHERE user_id = #{userId}
  AND out_business_no = #{outBusinessNo}
